import React, { useState, useRef } from 'react';
import { Stage, Layer, Image as KonvaImage, Rect } from 'react-konva';
import useImage from 'use-image';

const FloorplanImage = ({ src, onStageClick }) => {
  const [image] = useImage(src);

  return (
    <KonvaImage
      image={image}
      width={image ? 800 : 0}
      height={image ? 600 : 0}
      listening={false}
    />
  );
};

const AssetImage = ({ src, x, y, rotation = 0, scale = 1, onDragEnd, id }) => {
  const [image] = useImage(src);

  return (
    <KonvaImage
      id={id}
      image={image}
      x={x}
      y={y}
      width={image ? 60 * scale : 60}
      height={image ? 60 * scale : 60}
      rotation={rotation}
      draggable
      onDragEnd={onDragEnd}
      offsetX={image ? (60 * scale) / 2 : 30}
      offsetY={image ? (60 * scale) / 2 : 30}
    />
  );
};

const DesignCanvas = ({ 
  floorplanUrl, 
  selectedAsset, 
  sceneElements, 
  onElementPlace,
  onElementMove 
}) => {
  const stageRef = useRef();
  const [stageDimensions, setStageDimensions] = useState({ width: 800, height: 600 });

  const handleStageClick = (e) => {
    // Only place asset if we have a selected asset and clicked on empty area
    if (selectedAsset && e.target === e.target.getStage()) {
      const pos = e.target.getPointerPosition();
      onElementPlace({
        assetId: selectedAsset.id,
        positionX: pos.x,
        positionY: pos.y,
        rotationZ: 0,
        scaleX: 1,
        scaleY: 1
      });
    }
  };

  const handleAssetDragEnd = (elementId, e) => {
    const newPos = e.target.position();
    onElementMove(elementId, {
      positionX: newPos.x,
      positionY: newPos.y
    });
  };

  return (
    <div className="design-canvas">
      <div className="canvas-container" style={{ border: '1px solid #ccc', borderRadius: '4px' }}>
        <Stage
          ref={stageRef}
          width={stageDimensions.width}
          height={stageDimensions.height}
          onClick={handleStageClick}
          onTap={handleStageClick}
        >
          <Layer>
            {/* Background floorplan */}
            {floorplanUrl && (
              <FloorplanImage src={floorplanUrl} onStageClick={handleStageClick} />
            )}

            {/* Placed scene elements */}
            {sceneElements.map((element) => (
              <AssetImage
                key={element.id}
                id={element.id}
                src={element.asset_image_url}
                x={element.position_x}
                y={element.position_y}
                rotation={element.rotation_z}
                scale={element.scale_x}
                onDragEnd={(e) => handleAssetDragEnd(element.id, e)}
              />
            ))}

            {/* Preview of selected asset following cursor - could be added later */}
          </Layer>
        </Stage>
      </div>

      <div className="canvas-controls" style={{ marginTop: '10px', padding: '10px', backgroundColor: '#f5f5f5', borderRadius: '4px' }}>
        <p style={{ margin: '0', fontSize: '14px', color: '#666' }}>
          {selectedAsset 
            ? `Click on the canvas to place: ${selectedAsset.type}` 
            : 'Select an asset from the library to place it on the canvas'
          }
        </p>
      </div>
    </div>
  );
};

export default DesignCanvas;
